// Sanitize Data
function clean(string $value)
{
  $value = htmlspecialchars($value);
  $value = strip_tags($value);
  $value = trim($value);
  return $value;
}
// Get Object Value
function get(array $data, string $key)
{
  $value = $data[$key] ?? '';
  return  clean($value);
}

// Get Icon
function getIcon(string $name)
{
  return STORE::get($name);
  // return '';
}

