// Structure Body
function makeBody(string $string)
{
  $body = "";
  $items = explode("\n", $string);

  foreach ($items as $key => $value) {
    if (empty($value)) continue;
    if ($key === 0) $body .= '<p class="mb-x txt-justified">' . clean($value) . '</p>';
    else $body .= '<p class="p-list">' . clean($value) . '</p>';
  }
  return $body;
}
